   <template>
     <div class="bg-white p-6 rounded-lg shadow-md">
       <h2 class="text-2xl font-semibold text-gray-800 mb-4">Lista de Cartones</h2>
       <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
         <div v-for="carton in cartones" :key="carton.id" class="bg-gray-100 p-4 rounded-lg shadow text-center">
           <img :src="carton.imageUrl" alt="Carton" class="w-full h-48 object-cover mb-2 rounded">
           <p><strong>Serial:</strong> {{ carton.serial }}</p>
           <p><strong>Estado:</strong> {{ carton.estado }}</p>
         </div>
       </div>
     </div>
   </template>

   <script>
   import { db } from '../firebase';
   import { collection, query, where, getDocs, onSnapshot } from 'firebase/firestore';

   export default {
     data() {
       return {
         cartones: []
       };
     },
     async mounted() {
       await this.loadCartones();
       // Escuchar cambios en tiempo real
       this.setupRealtimeListener();
     },
     methods: {
       async loadCartones() {
         try {
           console.log('Cargando cartones...');
           const q = query(collection(db, 'cartones'));
           const querySnapshot = await getDocs(q);
           this.cartones = querySnapshot.docs.map(doc => ({
             id: doc.id,
             ...doc.data()
           }));
           console.log('Cartones cargados:', this.cartones);
         } catch (error) {
           console.error('Error al cargar cartones:', error);
         }
       },
       setupRealtimeListener() {
         const q = query(collection(db, 'cartones'));
         onSnapshot(q, (snapshot) => {
           this.cartones = snapshot.docs.map(doc => ({
             id: doc.id,
             ...doc.data()
           }));
           console.log('Cartones actualizados en tiempo real:', this.cartones);
         }, (error) => {
           console.error('Error en tiempo real:', error);
         });
       }
     }
   };
   </script>

   <style scoped>
   /* Tailwind maneja los estilos */
   </style>
   ```
