<template>
  <div class="bg-white p-6 rounded-lg shadow-md">
    <div class="flex justify-between items-center mb-4">
      <h2 class="text-xl font-semibold text-gray-800">Dashboard del Vendedor</h2>
      <button @click="logout" class="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition">Cerrar Sesión</button>
    </div>

    <!-- Estadísticas -->
    <div class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
      <div class="bg-gray-100 p-4 rounded-lg text-center">
        <p class="text-gray-600">Saldo</p>
        <p class="text-2xl font-bold text-green-600">${{ sellerSaldo }}</p>
      </div>
      <div class="bg-gray-100 p-4 rounded-lg text-center">
        <p class="text-gray-600">Cartones Asignados</p>
        <p class="text-2xl font-bold text-blue-600">{{ assignedCartons.length }}</p>
      </div>
      <div class="bg-gray-100 p-4 rounded-lg text-center">
        <p class="text-gray-600">Cartones Vendidos</p>
        <p class="text-2xl font-bold text-red-600">{{ soldCartons.length }}</p>
      </div>
      <div class="bg-gray-100 p-4 rounded-lg text-center">
        <p class="text-gray-600">Cartones Disponibles</p>
        <p class="text-2xl font-bold text-purple-600">{{ availableCartons.length }}</p>
      </div>
    </div>

    <!-- Botón para Vender Cartón -->
    <div class="mb-4">
      <button
        @click="startQRScanner"
        class="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition"
        :disabled="scanning"
      >
        <span v-if="scanning">
          <svg class="animate-spin h-5 w-5 mr-2 inline-block" viewBox="0 0 24 24">
            <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
            <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
          Escaneando...
        </span>
        <span v-else>Escanear QR para Vender</span>
      </button>
      <input
        v-model="manualSerial"
        type="text"
        placeholder="Ingresar Serial Manualmente"
        class="mt-2 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
        @keyup.enter="processManualSerial"
      />
    </div>

    <!-- Escáner de QR -->
    <div v-if="showScanner" class="relative">
      <video ref="video" autoplay playsinline class="w-full max-w-md rounded-lg"></video>
      <canvas ref="canvas" class="absolute top-0 left-0 w-full max-w-md opacity-0"></canvas>
      <p v-if="scanMessage" class="mt-2 text-center text-lg font-medium" :class="{ 'text-red-600': errorMessage, 'text-green-600': successMessage }">
        {{ scanMessage }}
      </p>
      <div v-if="showActionButtons" class="mt-4 text-center">
        <button @click="sellCarton" class="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 mr-2">Vender</button>
        <button @click="cancelScan" class="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600">Cancelar</button>
      </div>
    </div>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { signOut } from 'firebase/auth';
import { db } from '../firebase';
import { collection, query, where, getDocs, doc, updateDoc, onSnapshot } from 'firebase/firestore';
import jsQR from 'jsqr';

export default {
  data() {
    return {
      sellerId: '',
      sellerSaldo: 0,
      assignedCartons: [],
      soldCartons: [],
      availableCartons: [],
      scanning: false,
      showScanner: false,
      showActionButtons: false,
      scanMessage: '',
      errorMessage: false,
      successMessage: false,
      manualSerial: '',
      video: null,
      canvas: null,
      stream: null
    };
  },
  async mounted() {
    this.sellerId = auth.currentUser.uid;
    await this.loadSellerData();
    this.video = this.$refs.video;
    this.canvas = this.$refs.canvas.getContext('2d');
    this.loadSellerBalance();
  },
  beforeUnmount() {
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
    }
  },
  methods: {
    async loadSellerData() {
      try {
        const assignedQuery = query(
          collection(db, 'cartones'),
          where('vendedorId', '==', this.sellerId),
          where('estado', '==', 'asignado')
        );
        onSnapshot(assignedQuery, (snapshot) => {
          this.assignedCartons = snapshot.docs.map(doc => ({
            id: doc.id,
            serial: doc.data().serial
          })).sort((a, b) => parseInt(a.serial.replace('LM', '')) - parseInt(b.serial.replace('LM', '')));
        });

        const soldQuery = query(
          collection(db, 'cartones'),
          where('vendedorId', '==', this.sellerId),
          where('estado', '==', 'vendido')
        );
        onSnapshot(soldQuery, (snapshot) => {
          this.soldCartons = snapshot.docs.map(doc => ({
            id: doc.id,
            serial: doc.data().serial
          })).sort((a, b) => parseInt(a.serial.replace('LM', '')) - parseInt(b.serial.replace('LM', '')));
        });

        const availableQuery = query(
          collection(db, 'cartones'),
          where('vendedorId', '==', null),
          where('estado', '==', 'disponible')
        );
        onSnapshot(availableQuery, (snapshot) => {
          this.availableCartons = snapshot.docs.map(doc => ({
            id: doc.id,
            serial: doc.data().serial
          })).sort((a, b) => parseInt(a.serial.replace('LM', '')) - parseInt(b.serial.replace('LM', '')));
        });
      } catch (error) {
        console.error('Error al cargar datos del vendedor:', error);
        alert('Error al cargar datos: ' + error.message);
      }
    },
    loadSellerBalance() {
      const sellerRef = doc(db, 'vendedores', this.sellerId);
      onSnapshot(sellerRef, (docSnapshot) => {
        this.sellerSaldo = docSnapshot.data()?.saldoGenerado || 0;
      }, (error) => {
        console.error('Error al cargar saldo:', error);
        alert('Error al cargar saldo: ' + error.message);
      });
    },
    async startQRScanner() {
      if (this.scanning) return;
      this.scanning = true;
      this.showScanner = true;
      this.scanMessage = '';
      this.errorMessage = false;
      this.successMessage = false;
      this.showActionButtons = false;

      try {
        this.stream = await navigator.mediaDevices.getUserMedia({ video: { facingMode: 'environment' } });
        this.video.srcObject = this.stream;
        this.video.play();
        this.scanQRCode();
      } catch (error) {
        console.error('Error al acceder a la cámara:', error);
        this.scanMessage = 'Error al acceder a la cámara: ' + error.message;
        this.errorMessage = true;
        this.scanning = false;
      }
    },
    scanQRCode() {
      if (this.video.readyState === this.video.HAVE_ENOUGH_DATA) {
        this.canvas.drawImage(this.video, 0, 0, this.canvas.canvas.width, this.canvas.canvas.height);
        const imageData = this.canvas.getImageData(0, 0, this.canvas.canvas.width, this.canvas.canvas.height);
        const code = jsQR(imageData.data, imageData.width, imageData.height);

        if (code) {
          this.processSerial(code.data);
          this.stream.getTracks().forEach(track => track.stop());
          this.scanning = false;
        } else {
          requestAnimationFrame(this.scanQRCode);
        }
      } else {
        requestAnimationFrame(this.scanQRCode);
      }
    },
    async processSerial(serial) {
      const cartonDoc = await getDocs(query(collection(db, 'cartones'), where('serial', '==', serial)));
      if (cartonDoc.empty) {
        this.scanMessage = 'Cartón no encontrado.';
        this.errorMessage = true;
        return;
      }

      const cartonData = cartonDoc.docs[0].data();
      const cartonState = cartonData.estado;

      if (cartonState === 'vendido') {
        this.scanMessage = 'Cartón ya vendido.';
        this.errorMessage = true;
      } else if (cartonState === 'disponible' || (cartonState === 'asignado' && cartonData.vendedorId === this.sellerId)) {
        this.scanMessage = `Cartón ${serial} encontrado. ¿Deseas venderlo?`;
        this.showActionButtons = true;
      }
    },
    async sellCarton() {
      if (!this.scanMessage.includes('encontrado')) return;
      const serial = this.scanMessage.match(/Cartón (\w+)/)[1];
      const cartonDoc = await getDocs(query(collection(db, 'cartones'), where('serial', '==', serial)));
      const cartonId = cartonDoc.docs[0].id;

      try {
        await updateDoc(doc(db, 'cartones', cartonId), { estado: 'vendido' });
        const sellerRef = doc(db, 'vendedores', this.sellerId);
        const currentSaldo = this.sellerSaldo;
        await updateDoc(sellerRef, { saldoGenerado: currentSaldo + 1 });

        this.scanMessage = `Cartón ${serial} vendido exitosamente.`;
        this.successMessage = true;
        this.showActionButtons = false;
        this.loadSellerData();
        this.loadSellerBalance();
      } catch (error) {
        console.error('Error al vender el cartón:', error);
        this.scanMessage = 'Error al vender el cartón: ' + error.message;
        this.errorMessage = true;
      }
    },
    cancelScan() {
      if (this.stream) this.stream.getTracks().forEach(track => track.stop());
      this.scanning = false;
      this.showScanner = false;
      this.scanMessage = '';
      this.showActionButtons = false;
    },
    processManualSerial() {
      if (this.manualSerial) {
        this.processSerial(this.manualSerial);
        this.manualSerial = '';
      }
    },
    async logout() {
      try {
        await signOut(auth);
        this.$router.push('/login');
      } catch (error) {
        console.error('Error al cerrar sesión:', error);
        alert('Error al cerrar sesión: ' + error.message);
      }
    }
  }
};
</script>

<style scoped>
/* Tailwind maneja los estilos */
</style>
