    <!-- Login Form (visible if not authenticated) -->
    <div v-if="!user" class="min-h-screen flex items-center justify-center">
      <div class="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-500 opacity-0 animate-fade-in">
        <div class="text-center mb-6">
          <div class="flex justify-center mb-4">
            <span class="text-4xl font-bold text-indigo-600 animate-spin-slow">VC</span>
          </div>
          <h2 class="text-3xl font-bold text-gray-800 mb-2">Venta de Cartones</h2>
          <p class="text-gray-600">Inicia sesión para gestionar tus cartones</p>
        </div>
        <form @submit.prevent="login" class="space-y-6">
          <div>
            <label for="email" class="block text-sm font-medium text-gray-700">Correo Electrónico</label>
            <input
              v-model="email"
              type="email"
              id="email"
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="master1@example.com"
              required
            />
          </div>
          <div>
            <label for="password" class="block text-sm font-medium text-gray-700">Contraseña</label>
            <input
              v-model="password"
              type="password"
              id="password"
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Sagu7.98"
              required
            />
          </div>
          <button
            type="submit"
            :disabled="loading"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50 transition duration-300 flex items-center justify-center"
          >
            <span v-if="loading" class="flex items-center">
              <svg class="animate-spin h-5 w-5 mr-2 text-white" viewBox="0 0 24 24">
                <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
                <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Cargando...
            </span>
            <span v-else>Iniciar Sesión</span>
          </button>
          <p v-if="error" class="text-red-500 text-sm text-center">{{ error }}</p>
        </form>
        <div class="mt-4 text-center">
          <p class="text-gray-600 text-sm">¿Olvidaste tu contraseña? <a href="#" class="text-indigo-600 hover:underline transition duration-300">Recupérala</a></p>
        </div>
      </div>
    </div>

    <!-- Admin Dashboard (visible for master1) -->
    <div v-if="user && user.email === 'master1@example.com'" class="min-h-screen flex flex-col">
      <header class="bg-indigo-900 text-white p-4 flex justify-between items-center">
        <div class="flex items-center">
          <span class="text-2xl font-bold mr-2">VC</span>
          <h1 class="text-xl">Dashboard Admin</h1>
        </div>
        <button
          @click="logout"
          class="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition"
        >
          Cerrar Sesión
        </button>
      </header>

      <div class="p-6 grid grid-cols-1 md:grid-cols-2 gap-6">
        <!-- Subir Cartones -->
        <div class="bg-white p-6 rounded-lg shadow-md">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Subir Cartones</h2>
          <input
            type="file"
            accept="image/*"
            @change="handleFileUpload"
            class="mb-4 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-gray-100 file:text-gray-700 hover:file:bg-gray-200"
          />
          <button
            @click="uploadCartons"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
          >
            Subir Imágenes
          </button>
        </div>

        <!-- Asignar Cartones -->
        <div class="bg-white p-6 rounded-lg shadow-md">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Asignar Cartones por Lote</h2>
          <div class="mb-4">
            <label class="block text-sm font-medium text-gray-700">Seleccionar Vendedor</label>
            <select
              v-model="selectedSeller"
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
            >
              <option disabled value="">Selecciona un vendedor</option>
              <option v-for="seller in sellers" :key="seller.id" :value="seller.id">{{ seller.nombre }}</option>
            </select>
          </div>
          <div class="grid grid-cols-2 gap-4 mb-4">
            <div>
              <label class="block text-sm font-medium text-gray-700">Desde</label>
              <select
                v-model="cartonFrom"
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              >
                <option disabled value="">Selecciona un cartón</option>
                <option v-for="carton in availableCartons" :key="carton.id" :value="carton.id">{{ carton.serial }}</option>
              </select>
            </div>
            <div>
              <label class="block text-sm font-medium text-gray-700">Hasta</label>
              <select
                v-model="cartonTo"
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              >
                <option disabled value="">Selecciona un cartón</option>
                <option v-for="carton in availableCartons" :key="carton.id" :value="carton.id">{{ carton.serial }}</option>
              </select>
            </div>
          </div>
          <button
            @click="assignCartons"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
          >
            Asignar Seleccionados
          </button>
          <p class="text-yellow-600 text-sm mt-2">Por favor, selecciona un vendedor y un rango de cartones.</p>
        </div>

        <!-- Lista de Cartones -->
        <div class="bg-white p-6 rounded-lg shadow-md">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Lista de Cartones</h2>
          <div class="overflow-x-auto">
            <table class="min-w-full bg-white">
              <thead>
                <tr>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Serial</th>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Estado</th>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Vendedor</th>
                </tr>
              </thead>
              <tbody>
                <tr v-for="carton in cartons" :key="carton.id">
                  <td class="py-2 px-4 border-b">{{ carton.serial }}</td>
                  <td class="py-2 px-4 border-b">{{ carton.estado }}</td>
                  <td class="py-2 px-4 border-b">{{ carton.vendedorId ? sellers.find(s => s.id === carton.vendedorId)?.nombre || 'Sin asignar' : 'Sin asignar' }}</td>
                </tr>
              </tbody>
            </table>
          </div>
          <div class="flex space-x-4 mt-4">
            <button
              @click="refreshCartonList"
              class="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
            >
              Refrescar
            </button>
            <button
              @click="downloadReport"
              class="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
            >
              Descargar Reporte
            </button>
          </div>
        </div>

        <!-- Lista de Vendedores -->
        <div class="bg-white p-6 rounded-lg shadow-md">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Lista de Vendedores</h2>
          <div class="overflow-x-auto">
            <table class="min-w-full bg-white">
              <thead>
                <tr>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Nombre</th>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Saldo Generado</th>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Cartones Asignados</th>
                  <th class="py-2 px-4 border-b text-left text-gray-600">Acciones</th>
                </tr>
              </thead>
              <tbody>
                <tr v-for="seller in sellers" :key="seller.id">
                  <td class="py-2 px-4 border-b">{{ seller.nombre }}</td>
                  <td class="py-2 px-4 border-b">${{ seller.saldoGenerado }}</td>
                  <td class="py-2 px-4 border-b">{{ assignedCartons.filter(carton => carton.vendedorId === seller.id).length }}</td>
                  <td class="py-2 px-4 border-b">
                    <button
                      @click="viewSellerDetails(seller)"
                      class="bg-indigo-600 text-white px-4 py-1 rounded-lg hover:bg-indigo-700 transition"
                    >
                      Ver Detalles
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <button
            @click="showAddSellerModal = true"
            class="w-full mt-4 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition"
          >
            Agregar Vendedor
          </button>
        </div>

        <!-- Estadísticas -->
        <div class="bg-white p-6 rounded-lg shadow-md md:col-span-2">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Estadísticas</h2>
          <div class="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div class="text-center">
              <p class="text-gray-600">Cartones Disponibles</p>
              <p class="text-2xl font-bold text-gray-800">{{ availableCartons.length }}</p>
            </div>
            <div class="text-center">
              <p class="text-gray-600">Cartones Vendidos</p>
              <p class="text-2xl font-bold text-gray-800">{{ soldCartons.length }}</p>
            </div>
            <div class="text-center">
              <p class="text-gray-600">Vendedores</p>
              <p class="text-2xl font-bold text-gray-800">{{ sellers.length }}</p>
            </div>
            <div class="text-center">
              <p class="text-gray-600">Cartones Asignados</p>
              <p class="text-2xl font-bold text-gray-800">{{ assignedCartons.length }}</p>
            </div>
          </div>
        </div>
      </div>

      <!-- Modal para Agregar Vendedor -->
      <div v-if="showAddSellerModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div class="bg-white p-6 rounded-xl shadow-lg max-w-md w-full">
          <h3 class="text-xl font-semibold text-gray-800 mb-4">Agregar Nuevo Vendedor</h3>
          <form @submit.prevent="addSeller" class="space-y-4">
            <div>
              <label for="sellerEmail" class="block text-sm font-medium text-gray-700">Correo</label>
              <input
                v-model="newSellerEmail"
                id="sellerEmail"
                type="email"
                required
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                placeholder="vendedor1@example.com"
              />
            </div>
            <div>
              <label for="sellerPassword" class="block text-sm font-medium text-gray-700">Contraseña</label>
              <input
                v-model="newSellerPassword"
                id="sellerPassword"
                type="password"
                required
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                placeholder="Ingrese la contraseña"
              />
            </div>
            <div>
              <label for="sellerName" class="block text-sm font-medium text-gray-700">Nombre</label>
              <input
                v-model="newSellerName"
                id="sellerName"
                type="text"
                required
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                placeholder="Nombre del vendedor"
              />
            </div>
            <div class="flex justify-end space-x-4">
              <button
                type="button"
                @click="showAddSellerModal = false"
                class="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition"
              >
                Cancelar
              </button>
              <button
                type="submit"
                class="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition"
              >
                Agregar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>

    <!-- Redirección para Vendedores -->
    <div v-if="user && user.email.startsWith('vendedor')" class="min-h-screen flex items-center justify-center">
      <p class="text-center text-white text-lg">Redirigiendo...</p>
    </div>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { signInWithEmailAndPassword, onAuthStateChanged, signOut } from 'firebase/auth';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { db } from '../firebase';
import { doc, setDoc, collection, query, where, onSnapshot, getDocs, addDoc, updateDoc } from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';

export default {
  name: 'LoginPage',
  data() {
    return {
      email: '',
      password: '',
      error: '',
      loading: false,
      user: null,
      showAddSellerModal: false,
      newSellerEmail: '',
      newSellerPassword: '',
      newSellerName: '',
      selectedSeller: '',
      cartonFrom: '',
      cartonTo: '',
      sellers: [],
      cartons: [],
      availableCartons: [],
      assignedCartons: [],
      soldCartons: [],
      files: []
    };
  },
  mounted() {
    onAuthStateChanged(auth, (user) => {
      this.user = user;
      if (user) {
        if (user.email === 'master1@example.com') {
          this.loadSellers();
          this.loadCartons();
          this.loadCartonStats();
        } else if (user.email.startsWith('vendedor')) {
          this.$router.push('/vendedor');
        }
      }
    });
  },
  methods: {
    async login() {
      this.loading = true;
      this.error = '';
      try {
        console.log('Intentando iniciar sesión con:', this.email, this.password);
        await signInWithEmailAndPassword(auth, this.email, this.password);
        console.log('Sesión iniciada con éxito');
      } catch (error) {
        console.error('Error al iniciar sesión:', error.code, error.message);
        this.error = `Error: ${error.code} - ${error.message}`;
      } finally {
        this.loading = false;
      }
    },
    async addSeller() {
      if (!this.newSellerEmail || !this.newSellerPassword || !this.newSellerName) {
        alert('Por favor, complete todos los campos.');
        return;
      }

      try {
        const userCredential = await createUserWithEmailAndPassword(auth, this.newSellerEmail, this.newSellerPassword);
        const user = userCredential.user;
        await setDoc(doc(db, 'vendedores', user.uid), {
          email: this.newSellerEmail,
          nombre: this.newSellerName,
          saldoGenerado: 0
        });
        alert('Vendedor agregado exitosamente.');
        this.showAddSellerModal = false;
        this.newSellerEmail = '';
        this.newSellerPassword = '';
        this.newSellerName = '';
        this.loadSellers();
      } catch (error) {
        console.error('Error al agregar vendedor:', error);
        alert('Error al agregar vendedor: ' + error.message);
      }
    },
    async logout() {
      try {
        await signOut(auth);
        this.user = null;
        this.$router.push('/login');
      } catch (error) {
        console.error('Error al cerrar sesión:', error);
        alert('Error al cerrar sesión: ' + error.message);
      }
    },
    loadSellers() {
      const sellersQuery = query(collection(db, 'vendedores'));
      onSnapshot(sellersQuery, (snapshot) => {
        this.sellers = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
      });
    },
    loadCartons() {
      const cartonsQuery = query(collection(db, 'cartones'));
      onSnapshot(cartonsQuery, (snapshot) => {
        this.cartons = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
      });
    },
    loadCartonStats() {
      const availableQuery = query(
        collection(db, 'cartones'),
        where('vendedorId', '==', null),
        where('estado', '==', 'disponible')
      );
      onSnapshot(availableQuery, (snapshot) => {
        this.availableCartons = snapshot.docs.map(doc => ({
          id: doc.id,
          serial: doc.data().serial
        })).sort((a, b) => parseInt(a.serial.replace('LM', '')) - parseInt(b.serial.replace('LM', '')));
      });

      const assignedQuery = query(
        collection(db, 'cartones'),
        where('estado', '==', 'asignado')
      );
      onSnapshot(assignedQuery, (snapshot) => {
        this.assignedCartons = snapshot.docs.map(doc => ({
          id: doc.id,
          serial: doc.data().serial,
          vendedorId: doc.data().vendedorId
        }));
      });

      const soldQuery = query(
        collection(db, 'cartones'),
        where('estado', '==', 'vendido')
      );
      onSnapshot(soldQuery, (snapshot) => {
        this.soldCartons = snapshot.docs.map(doc => ({
          id: doc.id,
          serial: doc.data().serial
        }));
      });
    },
    handleFileUpload(event) {
      this.files = Array.from(event.target.files);
    },
    async uploadCartons() {
      if (this.files.length === 0) {
        alert('Por favor, selecciona al menos una imagen.');
        return;
      }

      const storage = getStorage();
      const uploadPromises = this.files.map(async (file, index) => {
        const serial = `LM${Date.now()}${index}`;
        const storageRef = ref(storage, `cartones/${serial}`);
        await uploadBytes(storageRef, file);
        const url = await getDownloadURL(storageRef);

        await addDoc(collection(db, 'cartones'), {
          serial: serial,
          url: url,
          estado: 'disponible',
          vendedorId: null,
          createdAt: new Date()
        });
      });

      try {
        await Promise.all(uploadPromises);
        alert('Cartones subidos exitosamente.');
        this.files = [];
      } catch (error) {
        console.error('Error al subir cartones:', error);
        alert('Error al subir cartones: ' + error.message);
      }
    },
    async assignCartons() {
      if (!this.selectedSeller || !this.cartonFrom || !this.cartonTo) {
        alert('Por favor, selecciona un vendedor y un rango de cartones.');
        return;
      }

      const fromIndex = this.availableCartons.findIndex(carton => carton.id === this.cartonFrom);
      const toIndex = this.availableCartons.findIndex(carton => carton.id === this.cartonTo);

      if (fromIndex === -1 || toIndex === -1 || fromIndex > toIndex) {
        alert('Rango de cartones inválido.');
        return;
      }

      const cartonsToAssign = this.availableCartons.slice(fromIndex, toIndex + 1);
      const updatePromises = cartonsToAssign.map(async (carton) => {
        await updateDoc(doc(db, 'cartones', carton.id), {
          vendedorId: this.selectedSeller,
          estado: 'asignado'
        });
      });

      try {
        await Promise.all(updatePromises);
        alert('Cartones asignados exitosamente.');
        this.selectedSeller = '';
        this.cartonFrom = '';
        this.cartonTo = '';
      } catch (error) {
        console.error('Error al asignar cartones:', error);
        alert('Error al asignar cartones: ' + error.message);
      }
    },
    refreshCartonList() {
      this.loadCartons();
      this.loadCartonStats();
    },
    async downloadReport() {
      const cartons = await getDocs(collection(db, 'cartones'));
      const reportData = cartons.docs.map(doc => {
        const data = doc.data();
        return `${data.serial},${data.estado},${data.vendedorId || 'Sin asignar'}`;
      }).join('\n');

      const blob = new Blob([`Serial,Estado,Vendedor\n${reportData}`], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'reporte_cartones.csv';
      a.click();
      window.URL.revokeObjectURL(url);
    },
    viewSellerDetails(seller) {
      alert(`Detalles del vendedor:\nNombre: ${seller.nombre}\nEmail: ${seller.email}\nSaldo Generado: $${seller.saldoGenerado}`);
    }
  }
};
</script>

