<template>
  <div class="min-h-screen bg-gray-100">
    <!-- Barra de Navegación -->
    <header class="bg-indigo-900 text-white shadow-lg">
      <div class="container mx-auto px-4 py-4 flex justify-between items-center">
        <div class="flex items-center space-x-3">
          <span class="text-2xl font-bold text-purple-300 animate-pulse">VC</span>
          <h1 class="text-2xl font-semibold">Dashboard Admin</h1>
        </div>
        <button
          @click="logout"
          class="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-opacity-50 transition duration-300"
        >
          Cerrar Sesión
        </button>
      </div>
    </header>

    <!-- Contenido Principal -->
    <main class="container mx-auto px-4 py-8">
      <div class="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <!-- Sección de Subir Cartones -->
        <div class="bg-white p-6 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl">
          <h2 class="text-xl font-semibold text-gray-800 mb-4">Subir Cartones</h2>
          <UploadCarton @check-duplicates="checkDuplicates" @images-uploaded="handleImagesUploaded" />
        </div>

        <!-- Sección de Asignar Cartones -->
        <div class="bg-white p-6 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl">
          <h2 class="text-xl font-semibold text-gray-800 mb-4">Asignar Cartones</h2>
          <AssignCartons @cartones-asignados="refreshCartones" />
        </div>

        <!-- Sección de Lista de Cartones -->
        <div class="bg-white p-6 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl">
          <h2 class="text-xl font-semibold text-gray-800 mb-4">Lista de Cartones</h2>
          <CartonList ref="cartonList" />
        </div>

        <!-- Sección de Lista de Vendedores -->
        <div class="bg-white p-6 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl">
          <h2 class="text-xl font-semibold text-gray-800 mb-4">Lista de Vendedores</h2>
          <SellerList />
        </div>
      </div>
    </main>
  </div>
</template>

<script>
import UploadCarton from '../components/UploadCarton.vue';
import AssignCartons from '../components/AssignCartons.vue';
import CartonList from '../components/CartonList.vue';
import SellerList from '../components/SellerList.vue';
import { db } from '../firebase';
import { collection, query, where, getDocs, addDoc } from 'firebase/firestore';
import { auth } from '../firebase';
import { signOut } from 'firebase/auth';

export default {
  name: 'HomePage',
  components: {
    UploadCarton,
    AssignCartons,
    CartonList,
    SellerList
  },
  methods: {
    async checkDuplicates({ serials, files }, callback) {
      try {
        const cartonesRef = collection(db, 'cartones');
        const q = query(cartonesRef, where('serial', 'in', serials));
        const querySnapshot = await getDocs(q);
        const existingSerials = querySnapshot.docs.map(doc => doc.data().serial);
        const nonDuplicates = files.filter(file => !existingSerials.includes(file.name.split('.')[0]));
        callback(nonDuplicates);
      } catch (error) {
        console.error('Error al verificar duplicados:', error);
        alert('Error al verificar duplicados: ' + error.message);
        callback([]);
      }
    },
    async handleImagesUploaded(images) {
      try {
        for (const image of images) {
          const docRef = await addDoc(collection(db, 'cartones'), {
            serial: image.serial,
            imageUrl: image.imageUrl,
            estado: 'disponible',
            createdAt: new Date(),
            vendedorId: null
          });
          console.log(`Cartón ${image.serial} guardado con ID:`, docRef.id);
        }
        alert('Todos los cartones se guardaron exitosamente en Firestore');
      } catch (error) {
        console.error('Error al guardar los cartones:', error);
        alert('Error al guardar en Firestore: ' + error.message);
      }
    },
    async refreshCartones() {
      this.$refs.cartonList.loadCartones();
    },
    async logout() {
      try {
        await signOut(auth);
        this.$router.push('/login');
        console.log('Sesión cerrada con éxito');
      } catch (error) {
        console.error('Error al cerrar sesión:', error);
        alert('Error al cerrar sesión: ' + error.message);
      }
    }
  }
};
</script>

<style scoped>
/* Animación de pulso para el logo */
.animate-pulse {
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
