    <div v-if="user && user.email === 'master1@example.com'">
      <header class="bg-indigo-900 text-white p-4 flex justify-between items-center">
        <div class="flex items-center">
          <span class="text-2xl font-bold mr-2">VC</span>
          <h1 class="text-xl">Dashboard Admin</h1>
        </div>
        <button
