            </select>
          </div>
          <div class="grid grid-cols-2 gap-4 mb-4">
            <div>
              <label class="block text-sm font-medium text-gray-700">Desde</label>
              <select
                v-model="cartonFrom"
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              >
                <option disabled value="">Selecciona un cartón</option>
                <option v-for="carton in availableCartons" :key="carton.id" :value="carton.id">{{ carton.serial }}</option>
              </select>
            </div>
            <div>
              <label class="block text-sm font-medium text-gray-700">Hasta</label>
              <select
                v-model="cartonTo"
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              >
                <option disabled value="">Selecciona un cartón</option>
                <option v-for="carton in availableCartons" :key="carton.id" :value="carton.id">{{ carton.serial }}</option>
              </select>
            </div>
          </div>
          <button
            @click="assignCartons"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
          >
            Asignar Seleccionados
          </button>
