            <div>
              <label for="sellerPassword" class="block text-sm font-medium text-gray-700">Contraseña</label>
              <input
                v-model="newSellerPassword"
                id="sellerPassword"
                type="password"
                required
                class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                placeholder="Ingrese la contraseña"
