   <template>
     <div class="bg-white p-6 rounded-lg shadow-md mb-6">
       <h2 class="text-2xl font-semibold text-gray-800 mb-4">Asignar Cartones por Lote</h2>
       <div class="space-y-4">
         <div>
           <label class="block text-gray-600 mb-1">Seleccionar Vendedor</label>
           <select
             v-model="selectedVendedorId"
             @change="logSelection"
             class="w-full p-2 border rounded-md"
           >
             <option value="" disabled>Selecciona un vendedor</option>
             <option v-for="seller in sellers" :key="seller.vendedorId" :value="seller.vendedorId">
               {{ seller.nombre || seller.vendedorId }}
             </option>
           </select>
         </div>
         <div>
           <label class="block text-gray-600 mb-1">Seleccionar Cartones</label>
           <div v-if="availableCartons.length > 0" class="space-y-2 max-h-60 overflow-y-auto border rounded-md p-2">
             <label v-for="carton in availableCartons" :key="carton.id" class="flex items-center space-x-2">
               <input type="checkbox" v-model="selectedCartons" :value="carton.id">
               <span>{{ carton.serial }}</span>
             </label>
           </div>
           <p v-else class="text-gray-500">No hay cartones disponibles.</p>
         </div>
         <button
           @click="assignSelectedCartons"
           class="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
           :disabled="!selectedVendedorId || selectedCartons.length === 0"
         >
           Asignar Seleccionados
         </button>
         <p v-if="!selectedVendedorId || selectedCartons.length === 0" class="text-yellow-600 mt-2">Por favor, selecciona un vendedor y al menos un cartón.</p>
       </div>
       <p v-if="errorMessage" class="text-red-600 mt-2">{{ errorMessage }}</p>
       <p v-if="successMessage" class="text-green-600 mt-2">{{ successMessage }}</p>
     </div>
   </template>

   <script>
   import { db } from '../firebase';
   import { collection, query, where, getDocs, doc, getDoc, updateDoc } from 'firebase/firestore';

   export default {
     data() {
       return {
         sellers: [],
         availableCartons: [],
         selectedVendedorId: '',
         selectedCartons: [],
         errorMessage: '',
         successMessage: ''
       };
     },
     async mounted() {
       console.log('Componente AssignCartons montado');
       await this.loadSellers();
       await this.loadAvailableCartons();
     },
     methods: {
       logSelection() {
         console.log('Selección actualizada:', {
           selectedVendedorId: this.selectedVendedorId,
           selectedCartons: this.selectedCartons
         });
       },
       async loadSellers() {
         try {
           console.log('Iniciando carga de vendedores...');
           const querySnapshot = await getDocs(collection(db, 'vendedores'));
           this.sellers = querySnapshot.docs.map(doc => doc.data());
           console.log('Vendedores cargados:', this.sellers);
         } catch (error) {
           console.error('Error al cargar los vendedores:', error);
           this.errorMessage = 'Error al cargar los vendedores: ' + error.message;
         }
       },
       async loadAvailableCartons() {
         try {
           console.log('Cargando cartones disponibles...');
           const q = query(
             collection(db, 'cartones'),
             where('estado', '==', 'disponible'),
             where('vendedorId', '==', null)
           );
           const querySnapshot = await getDocs(q);
           this.availableCartons = querySnapshot.docs.map(doc => ({
             id: doc.id,
             serial: doc.data().serial
           })).sort((a, b) => a.serial.localeCompare(b.serial));
           console.log('Cartones disponibles cargados (ordenados):', this.availableCartons);
         } catch (error) {
           console.error('Error al cargar los cartones disponibles:', error);
           this.errorMessage = 'Error al cargar los cartones disponibles: ' + error.message;
         }
       },
       async assignSelectedCartons() {
         console.log('Botón "Asignar Seleccionados" clicado');
         console.log('Valores seleccionados:', {
           selectedVendedorId: this.selectedVendedorId,
           selectedCartons: this.selectedCartons
         });

         this.errorMessage = '';
         this.successMessage = '';

         if (!this.selectedVendedorId || this.selectedCartons.length === 0) {
           console.log('Campos incompletos o sin cartones seleccionados');
           this.errorMessage = 'Por favor, selecciona un vendedor y al menos un cartón.';
           return;
         }

         try {
           console.log('Iniciando proceso de asignación...');
           const cartonesToAssign = this.availableCartons.filter(carton => this.selectedCartons.includes(carton.id));
           console.log('Cartones a asignar:', cartonesToAssign);

           if (cartonesToAssign.length === 0) {
             console.log('No hay cartones seleccionados para asignar');
             this.errorMessage = 'No hay cartones seleccionados para asignar.';
             return;
           }

           // Verificar si algún cartón ya está asignado
           let alreadyAssignedSerials = [];
           for (const carton of cartonesToAssign) {
             console.log(`Verificando cartón ${carton.serial}...`);
             const cartonRef = doc(db, 'cartones', carton.id);
             const cartonDoc = await getDoc(cartonRef);
             if (cartonDoc.exists() && cartonDoc.data().vendedorId && cartonDoc.data().vendedorId !== this.selectedVendedorId) {
               alreadyAssignedSerials.push(carton.serial);
             }
           }

           if (alreadyAssignedSerials.length > 0) {
             console.log('Cartones ya asignados detectados:', alreadyAssignedSerials);
             this.errorMessage = `Los siguientes cartones ya están asignados a otro vendedor: ${alreadyAssignedSerials.join(', ')}`;
             return;
           }

           // Asignar los cartones
           console.log('Procediendo a asignar cartones...');
           for (const carton of cartonesToAssign) {
             console.log(`Asignando cartón ${carton.serial} a ${this.selectedVendedorId}`);
             await updateDoc(doc(db, 'cartones', carton.id), {
               vendedorId: this.selectedVendedorId
             });
             console.log(`Cartón ${carton.serial} asignado exitosamente`);
           }

           console.log('Asignación completada para', cartonesToAssign.length, 'cartones');
           this.successMessage = 'Cartones asignados';
           this.selectedCartons = [];
           this.selectedVendedorId = '';
           // Recargar los cartones disponibles
           await this.loadAvailableCartons();
         } catch (error) {
           console.error('Error al asignar cartones:', error);
           this.errorMessage = 'Error al asignar cartones: ' + error.message;
         }
       }
     }
   };
   </script>

   <style scoped>
   /* Tailwind maneja los estilos */
   </style>
