<template>
  <div class="bg-white p-6 rounded-lg shadow-md max-w-md mx-auto mt-10">
    <h2 class="text-2xl font-semibold text-gray-800 mb-4 text-center">Iniciar Sesión</h2>
    <form @submit.prevent="login" class="space-y-4">
      <div>
        <label for="email" class="block text-sm font-medium text-gray-700">Correo</label>
        <input
          v-model="email"
          id="email"
          type="email"
          required
          class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
          placeholder="Ingrese su correo"
        />
      </div>
      <div>
        <label for="password" class="block text-sm font-medium text-gray-700">Contraseña</label>
        <input
          v-model="password"
          id="password"
          type="password"
          required
          class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
          placeholder="Ingrese su contraseña"
        />
      </div>
      <button
        type="submit"
        class="w-full bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition"
      >
        Iniciar Sesión
      </button>
    </form>
    <p v-if="error" class="mt-2 text-red-600 text-center">{{ error }}</p>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { signInWithEmailAndPassword, onAuthStateChanged } from 'firebase/auth';
import { useRouter } from 'vue-router';

export default {
  data() {
    return {
      email: '',
      password: '',
      error: ''
    };
  },
  setup() {
    const router = useRouter();
    return { router };
  },
  mounted() {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        if (user.email === 'master1@example.com') {
          this.router.push('/admin');
        } else if (user.email.startsWith('vendedor')) {
          this.router.push('/vendedor');
        }
      }
    });
  },
  methods: {
    async login() {
      try {
        await signInWithEmailAndPassword(auth, this.email, this.password);
      } catch (error) {
        this.error = 'Error al iniciar sesión: ' + error.message;
      }
    }
  }
};
</script>

