<template>
  <div class="bg-gray-100 min-h-screen flex items-center justify-center">
    <div class="bg-white p-6 rounded-lg shadow-md w-full max-w-md">
      <h2 class="text-2xl font-semibold text-gray-800 mb-4 text-center">Iniciar Sesión</h2>
      <form @submit.prevent="login">
        <div class="mb-4">
          <label for="email" class="block text-sm font-medium text-gray-700">Correo</label>
          <input
            v-model="email"
            type="email"
            id="email"
            class="mt-1 block w-full border-gray-300 rounded-md shadow-sm"
            placeholder="master1@example.com"
            required
          />
        </div>
        <div class="mb-4">
          <label for="password" class="block text-sm font-medium text-gray-700">Contraseña</label>
          <input
            v-model="password"
            type="password"
            id="password"
            class="mt-1 block w-full border-gray-300 rounded-md shadow-sm"
            placeholder="Sagu7.98"
            required
          />
        </div>
        <button
          type="submit"
          class="w-full bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition"
        >
          Iniciar Sesión
        </button>
        <p v-if="error" class="text-red-500 text-sm mt-2">{{ error }}</p>
      </form>
    </div>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { signInWithEmailAndPassword } from 'firebase/auth';

export default {
  data() {
    return {
      email: '',
      password: '',
      error: ''
    };
  },
  methods: {
    async login() {
      try {
        console.log('Intentando iniciar sesión con:', this.email, this.password);
        const userCredential = await signInWithEmailAndPassword(auth, this.email, this.password);
        console.log('Sesión iniciada con éxito:', userCredential.user);
        this.$router.push('/'); // Redirigir a la página principal
      } catch (error) {
        console.error('Error al iniciar sesión:', error.message);
        this.error = 'Correo o contraseña incorrectos. Intenta de nuevo.';
      }
    }
  }
};
</script>

<style scoped>
/* Tailwind maneja los estilos */
</style>
