```vue
<template>
  <div>
    <div class="flex justify-between items-center mb-4">
      <h2 class="text-xl font-semibold text-gray-800">Gestión de Vendedores</h2>
      <button
        @click="showAddModal = true"
        class="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition"
      >
        Agregar Vendedor
      </button>
    </div>
    <!-- Lista de Vendedores -->
    <div v-if="loading" class="text-center text-gray-600">Cargando vendedores...</div>
    <div v-else-if="sellers.length === 0" class="text-gray-600">No hay vendedores registrados.</div>
    <div v-else class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
      <div
        v-for="seller in sellers"
        :key="seller.id"
        class="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition"
      >
        <p class="text-gray-800 font-medium"><strong>Nombre:</strong> {{ seller.nombre }}</p>
        <p class="text-gray-600"><strong>ID:</strong> {{ seller.id }}</p>
        <p class="text-gray-600"><strong>Cartones Asignados:</strong> {{ seller.assignedCartons }}</p>
      </div>
    </div>
    <!-- Modal para Agregar Vendedor -->
    <div v-if="showAddModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div class="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
        <h3 class="text-xl font-semibold text-gray-800 mb-4">Agregar Nuevo Vendedor</h3>
        <form @submit.prevent="addSeller" class="space-y-4">
          <div>
            <label for="sellerName" class="block text-sm font-medium text-gray-700">Nombre</label>
            <input
              v-model="newSellerName"
              id="sellerName"
              type="text"
              required
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Ingrese el nombre del vendedor"
            />
          </div>
          <div class="flex justify-end space-x-4">
            <button
              type="button"
              @click="showAddModal = false"
              class="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition"
            >
              Cancelar
            </button>
            <button
              type="submit"
              class="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition"
            >
              Agregar
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</template>

<script>
import { db } from '../firebase';
import { collection, getDocs, query, where } from 'firebase/firestore';

export default {
  data() {
    return {
      sellers: [],
      loading: false,
      showAddModal: false,
      newSellerName: ''
    };
  },
  async mounted() {
    await this.loadSellers();
  },
  methods: {
    async loadSellers() {
      this.loading = true;
      try {
        const sellersSnapshot = await getDocs(collection(db, 'vendedores'));
        this.sellers = await Promise.all(sellersSnapshot.docs.map(async (doc) => {
          const sellerData = { id: doc.id, ...doc.data() };
          // Contar cartones asignados a este vendedor
          const cartonsQuery = query(
            collection(db, 'cartones'),
            where('vendedorId', '==', sellerData.id),
            where('estado', '==', 'asignado')
          );
          const cartonsSnapshot = await getDocs(cartonsQuery);
          sellerData.assignedCartons = cartonsSnapshot.size;
          return sellerData;
        }));
        console.log('Vendedores cargados con estadísticas:', this.sellers);
      } catch (error) {
        console.error('Error al cargar vendedores:', error);
        alert('Error al cargar vendedores: ' + error.message);
      } finally {
        this.loading = false;
      }
    },
    async addSeller() {
      if (!this.newSellerName.trim()) {
        alert('Por favor, ingrese un nombre válido.');
        return;
      }

      try {
        await addDoc(collection(db, 'vendedores'), {
          nombre: this.newSellerName.trim()
        });
        console.log('Vendedor agregado exitosamente');
        alert('Vendedor agregado exitosamente');
        this.newSellerName = '';
        this.showAddModal = false;
        await this.loadSellers();
      } catch (error) {
        console.error('Error al agregar vendedor:', error);
        alert('Error al agregar vendedor: ' + error.message);
      }
    }
  }
};
</script>

<style scoped>
/* Tailwind maneja los estilos */
</style>
