<template>
  <div>
    <!-- Lista de Cartones como Miniaturas -->
    <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
      <div
        v-for="carton in cartones"
        :key="carton.id"
        class="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition duration-300"
      >
        <!-- Miniatura -->
        <div class="w-full flex justify-center">
          <div class="w-32 h-32 sm:w-16 sm:h-16 lg:w-32 lg:h-32 bg-gray-200 rounded flex items-center justify-center text-gray-500">
            <img v-if="carton.imageUrl" :src="carton.imageUrl" alt="Cartón" class="w-full h-full object-cover rounded" />
            <span v-else>Cartón {{ carton.serial }}</span>
          </div>
        </div>
        <!-- Información del Cartón -->
        <div class="mt-2 text-center">
          <p class="text-gray-800 font-medium">Cartón {{ carton.serial }}</p>
          <p class="text-gray-600 text-sm">{{ carton.estado }}</p>
          <p v-if="carton.vendedorId" class="text-gray-600 text-sm">Vendedor: {{ getSellerName(carton.vendedorId) }}</p>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { db } from '../firebase';
import { collection, query, getDocs } from 'firebase/firestore';

export default {
  name: 'CartonList',
  data() {
    return {
      cartones: [],
      sellers: []
    };
  },
  async created() {
    await this.loadSellers();
    await this.loadCartones();
  },
  methods: {
    async loadSellers() {
      try {
        const querySnapshot = await getDocs(collection(db, 'vendedores'));
        this.sellers = querySnapshot.docs.map(docSnapshot => ({
          id: docSnapshot.id,
          name: docSnapshot.data().name || docSnapshot.id
        }));
      } catch (error) {
        console.error('Error al cargar vendedores:', error);
      }
    },
    async loadCartones() {
      try {
        const querySnapshot = await getDocs(collection(db, 'cartones'));
        this.cartones = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
      } catch (error) {
        console.error('Error al cargar cartones:', error);
      }
    },
    getSellerName(vendedorId) {
      const seller = this.sellers.find(s => s.id === vendedorId);
      return seller ? seller.name : 'Desconocido';
    }
  }
};
</script>

<style scoped>
/* Tailwind maneja los estilos */
</style>
