<template>
  <div class="min-h-screen bg-gray-100">
    <!-- Login Form (visible if not authenticated) -->
    <div v-if="!user" class="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-900 to-purple-300">
      <div class="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-500 opacity-0 animate-fade-in">
        <div class="text-center mb-6">
          <div class="flex justify-center mb-4">
            <span class="text-4xl font-bold text-indigo-600 animate-spin-slow">VC</span>
          </div>
          <h2 class="text-3xl font-bold text-gray-800 mb-2">Venta de Cartones</h2>
          <p class="text-gray-600">Inicia sesión para gestionar tus cartones</p>
        </div>
        <form @submit.prevent="login" class="space-y-6">
          <div>
            <label for="email" class="block text-sm font-medium text-gray-700">Correo Electrónico</label>
            <input
              v-model="email"
              type="email"
              id="email"
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="master1@example.com"
              required
            />
          </div>
          <div>
            <label for="password" class="block text-sm font-medium text-gray-700">Contraseña</label>
            <input
              v-model="password"
              type="password"
              id="password"
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Sagu7.98"
              required
            />
          </div>
          <button
            type="submit"
            :disabled="loading"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50 transition duration-300 flex items-center justify-center"
          >
            <span v-if="loading" class="flex items-center">
              <svg class="animate-spin h-5 w-5 mr-2 text-white" viewBox="0 0 24 24">
                <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
                <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Cargando...
            </span>
            <span v-else>Iniciar Sesión</span>
          </button>
          <p v-if="error" class="text-red-500 text-sm text-center">{{ error }}</p>
        </form>
      </div>
    </div>

    <!-- Admin Dashboard (visible for master1) -->
    <div v-if="user && user.email === 'master1@example.com'">
      <header class="bg-indigo-900 text-white p-4 flex justify-between items-center">
        <div class="flex items-center">
          <span class="text-2xl font-bold mr-2">VC</span>
          <h1 class="text-xl">Dashboard Admin</h1>
        </div>
        <button
          @click="logout"
          class="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition"
        >
          Cerrar Sesión
        </button>
      </header>

      <div class="p-6 grid grid-cols-1 md:grid-cols-2 gap-6">
        <!-- Subir Cartones -->
        <div class="bg-white p-6 rounded-lg shadow-md">
          <h2 class="text-lg font-semibold text-gray-800 mb-4">Subir Cartones</h2>
          <input
            type="file"
            accept="image/*"
            @change="handleFileUpload"
            class="mb-4 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-gray-100 file:text-gray-700 hover:file:bg-gray-200"
          />
          <button
            @click="uploadCartons"
            class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
