<template>
  <div class="bg-white p-6 rounded-lg shadow-md">
    <div class="flex justify-between items-center mb-4">
      <h2 class="text-xl font-semibold text-gray-800">Dashboard del Administrador</h2>
      <button @click="logout" class="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition">Cerrar Sesión</button>
    </div>
    <button
      @click="showAddSellerModal = true"
      class="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition mb-4"
    >
      Agregar Vendedor
    </button>

    <!-- Modal para Agregar Vendedor -->
    <div v-if="showAddSellerModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div class="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
        <h3 class="text-xl font-semibold text-gray-800 mb-4">Agregar Nuevo Vendedor</h3>
        <form @submit.prevent="addSeller" class="space-y-4">
          <div>
            <label for="sellerEmail" class="block text-sm font-medium text-gray-700">Correo</label>
            <input
              v-model="newSellerEmail"
              id="sellerEmail"
              type="email"
              required
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Ingrese el correo del vendedor (ej. vendedor1@example.com)"
            />
          </div>
          <div>
            <label for="sellerPassword" class="block text-sm font-medium text-gray-700">Contraseña</label>
            <input
              v-model="newSellerPassword"
              id="sellerPassword"
              type="password"
              required
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Ingrese la contraseña"
            />
          </div>
          <div>
            <label for="sellerName" class="block text-sm font-medium text-gray-700">Nombre</label>
            <input
              v-model="newSellerName"
              id="sellerName"
              type="text"
              required
              class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg shadow-md focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
              placeholder="Ingrese el nombre del vendedor"
            />
          </div>
          <div class="flex justify-end space-x-4">
            <button
              type="button"
              @click="showAddSellerModal = false"
              class="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition"
            >
              Cancelar
            </button>
            <button
              type="submit"
              class="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition"
            >
              Agregar
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { db } from '../firebase';
import { doc, setDoc } from 'firebase/firestore';

export default {
  data() {
    return {
      showAddSellerModal: false,
      newSellerEmail: '',
      newSellerPassword: '',
      newSellerName: ''
    };
  },
  methods: {
    async addSeller() {
      if (!this.newSellerEmail || !this.newSellerPassword || !this.newSellerName) {
        alert('Por favor, complete todos los campos.');
        return;
      }

      try {
        const userCredential = await createUserWithEmailAndPassword(auth, this.newSellerEmail, this.newSellerPassword);
        const user = userCredential.user;
        await setDoc(doc(db, 'vendedores', user.uid), {
          email: this.newSellerEmail,
          nombre: this.newSellerName,
          saldoGenerado: 0
        });
        alert('Vendedor agregado exitosamente.');
        this.showAddSellerModal = false;
        this.newSellerEmail = '';
        this.newSellerPassword = '';
        this.newSellerName = '';
      } catch (error) {
        console.error('Error al agregar vendedor:', error);
        alert('Error al agregar vendedor: ' + error.message);
      }
    },
    async logout() {
      try {
        await signOut(auth);
        this.$router.push('/login');
      } catch (error) {
        console.error('Error al cerrar sesión:', error);
        alert('Error al cerrar sesión: ' + error.message);
      }
    }
  }
};
</script>

<style scoped>
/* Tailwind maneja los estilos */
</style>
