<template>
  <div class="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-900 to-purple-300">
    <div class="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-500 opacity-0 animate-fade-in">
      <div class="text-center mb-6">
        <div class="flex justify-center mb-4">
          <span class="text-4xl font-bold text-indigo-600 animate-spin-slow">VC</span>
        </div>
        <h2 class="text-3xl font-bold text-gray-800 mb-2">Venta de Cartones</h2>
        <p class="text-gray-600" v-if="!user">Inicia sesión para gestionar tus cartones</p>
        <p class="text-gray-600" v-if="user && user.email === 'master1@example.com'">Bienvenido, Admin ({{ user.email }})</p>
        <p class="text-gray-600" v-if="user && user.email.startsWith('vendedor')">Bienvenido, Vendedor ({{ user.email }})</p>
      </div>

      <!-- Formulario de Login (visible si no hay usuario autenticado) -->
      <form v-if="!user" @submit.prevent="login" class="space-y-6">
        <div>
          <label for="email" class="block text-sm font-medium text-gray-700">Correo Electrónico</label>
          <input
            v-model="email"
            type="email"
            id="email"
            class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
            placeholder="master1@example.com"
            required
          />
        </div>
        <div>
          <label for="password" class="block text-sm font-medium text-gray-700">Contraseña</label>
          <input
            v-model="password"
            type="password"
            id="password"
            class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
            placeholder="Sagu7.98"
            required
          />
        </div>
        <button
          type="submit"
          :disabled="loading"
          class="w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50 transition duration-300 flex items-center justify-center"
        >
          <span v-if="loading" class="flex items-center">
            <svg class="animate-spin h-5 w-5 mr-2 text-white" viewBox="0 0 24 24">
              <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
              <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            Cargando...
          </span>
          <span v-else>Iniciar Sesión</span>
        </button>
        <p v-if="error" class="text-red-500 text-sm text-center">{{ error }}</p>
      </form>

      <!-- Contenido para Admin (master1) después de login -->
      <div v-if="user && user.email === 'master1@example.com'" class="space-y-6">
        <button
          @click="showAddSellerModal = true"
          class="w-full bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50 transition duration-300"
        >
          Agregar Vendedor
        </button>

        <!-- Modal para Agregar Vendedor -->
        <div v-if="showAddSellerModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div class="bg-white p-6 rounded-xl shadow-lg max-w-md w-full">
            <h3 class="text-xl font-semibold text-gray-800 mb-4">Agregar Nuevo Vendedor</h3>
            <form @submit.prevent="addSeller" class="space-y-4">
              <div>
                <label for="sellerEmail" class="block text-sm font-medium text-gray-700">Correo</label>
                <input
                  v-model="newSellerEmail"
                  id="sellerEmail"
                  type="email"
                  required
                  class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                  placeholder="vendedor1@example.com"
                />
              </div>
              <div>
                <label for="sellerPassword" class="block text-sm font-medium text-gray-700">Contraseña</label>
                <input
                  v-model="newSellerPassword"
                  id="sellerPassword"
                  type="password"
                  required
                  class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                  placeholder="Ingrese la contraseña"
                />
              </div>
              <div>
                <label for="sellerName" class="block text-sm font-medium text-gray-700">Nombre</label>
                <input
                  v-model="newSellerName"
                  id="sellerName"
                  type="text"
                  required
                  class="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition duration-300"
                  placeholder="Nombre del vendedor"
                />
              </div>
              <div class="flex justify-end space-x-4">
                <button
                  type="button"
                  @click="showAddSellerModal = false"
                  class="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600 transition"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  class="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition"
                >
                  Agregar
                </button>
              </div>
            </form>
          </div>
        </div>

        <button
          @click="logout"
          class="w-full bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50 transition duration-300"
        >
          Cerrar Sesión
        </button>
      </div>

      <!-- Redirección para Vendedores -->
      <div v-if="user && user.email.startsWith('vendedor')">
        <p class="text-center text-gray-600">Redirigiendo...</p>
      </div>
    </div>
  </div>
</template>

<script>
import { auth } from '../firebase';
import { signInWithEmailAndPassword, onAuthStateChanged, signOut } from 'firebase/auth';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { db } from '../firebase';
import { doc, setDoc } from 'firebase/firestore';

export default {
  name: 'LoginPage',
  data() {
    return {
      email: '',
      password: '',
      error: '',
      loading: false,
      user: null,
      showAddSellerModal: false,
      newSellerEmail: '',
      newSellerPassword: '',
      newSellerName: ''
    };
  },
  mounted() {
    onAuthStateChanged(auth, (user) => {
      this.user = user;
      if (user) {
        if (user.email === 'master1@example.com') {
          // Mantener en LoginPage.vue como dashboard del admin
        } else if (user.email.startsWith('vendedor')) {
          this.$router.push('/vendedor');
        }
      }
    });
  },
  methods: {
    async login() {
      this.loading = true;
      this.error = '';
      try {
        console.log('Intentando iniciar sesión con:', this.email, this.password);
        await signInWithEmailAndPassword(auth, this.email, this.password);
        console.log('Sesión iniciada con éxito');
      } catch (error) {
        console.error('Error al iniciar sesión:', error.code, error.message);
        this.error = `Error: ${error.code} - ${error.message}`;
      } finally {
        this.loading = false;
      }
    },
    async addSeller() {
      if (!this.newSellerEmail || !this.newSellerPassword || !this.newSellerName) {
        alert('Por favor, complete todos los campos.');
        return;
      }

      try {
        const userCredential = await createUserWithEmailAndPassword(auth, this.newSellerEmail, this.newSellerPassword);
        const user = userCredential.user;
        await setDoc(doc(db, 'vendedores', user.uid), {
          email: this.newSellerEmail,
          nombre: this.newSellerName,
          saldoGenerado: 0
        });
        alert('Vendedor agregado exitosamente.');
        this.showAddSellerModal = false;
        this.newSellerEmail = '';
        this.newSellerPassword = '';
        this.newSellerName = '';
      } catch (error) {
        console.error('Error al agregar vendedor:', error);
        alert('Error al agregar vendedor: ' + error.message);
      }
    },
    async logout() {
      try {
        await signOut(auth);
        this.user = null;
        this.$router.push('/login');
      } catch (error) {
        console.error('Error al cerrar sesión:', error);
        alert('Error al cerrar sesión: ' + error.message);
      }
    }
  }
};
</script>

<style scoped>
/* Animación de fade-in para la tarjeta */
.animate-fade-in {
  animation: fadeIn 0.5s ease-in-out forwards;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Animación de rotación lenta para el logo */
.animate-spin-slow {
  animation: spin 2s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
</style>
