   <template>
     <div class="bg-white p-6 rounded-lg shadow-md mb-6">
       <h2 class="text-2xl font-semibold text-gray-800 mb-4">Asignar Cartones por Lote</h2>
       <div class="space-y-4">
         <div>
           <label class="block text-gray-600 mb-1">Seleccionar Vendedor</label>
           <select
             v-model="selectedVendedorId"
             class="w-full p-2 border rounded-md"
           >
             <option value="" disabled>Selecciona un vendedor</option>
             <option v-for="seller in sellers" :key="seller.vendedorId" :value="seller.vendedorId">
               {{ seller.nombre || seller.vendedorId }}
             </option>
           </select>
         </div>
         <div>
           <label class="block text-gray-600 mb-1">Serial Inicial (ej. LM0001)</label>
           <input
             v-model="startSerial"
             type="text"
             placeholder="LM0001"
             class="w-full p-2 border rounded-md"
           />
         </div>
         <div>
           <label class="block text-gray-600 mb-1">Serial Final (ej. LM0100)</label>
           <input
             v-model="endSerial"
             type="text"
             placeholder="LM0100"
             class="w-full p-2 border rounded-md"
           />
         </div>
         <button
           @click="assignCartons"
           class="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
           :disabled="!selectedVendedorId || !startSerial || !endSerial"
         >
           Asignar Lote
         </button>
       </div>
       <p v-if="errorMessage" class="text-red-600 mt-2">{{ errorMessage }}</p>
       <p v-if="successMessage" class="text-green-600 mt-2">{{ successMessage }}</p>
     </div>
   </template>

   <script>
   import { db } from '../firebase';
   import { doc, getDoc, updateDoc } from 'firebase/firestore';

   export default {
     data() {
       return {
         sellers: [],
         selectedVendedorId: '',
         startSerial: '',
         endSerial: '',
         errorMessage: '',
         successMessage: ''
       };
     },
     async mounted() {
       console.log('Componente AssignCartons montado');
       await this.loadSellers();
     },
     methods: {
       async loadSellers() {
         try {
           console.log('Iniciando carga de vendedores...');
           const { collection, getDocs } = await import('firebase/firestore');
           const querySnapshot = await getDocs(collection(db, 'vendedores'));
           this.sellers = querySnapshot.docs.map(doc => doc.data());
           console.log('Vendedores cargados en AssignCartons:', this.sellers);
         } catch (error) {
           console.error('Error al cargar los vendedores:', error);
           this.errorMessage = 'Error al cargar los vendedores: ' + error.message;
         }
       },
       async assignCartons() {
         console.log('Función assignCartons ejecutada');
         console.log('Valores del formulario:', {
           selectedVendedorId: this.selectedVendedorId,
           startSerial: this.startSerial,
           endSerial: this.endSerial
         });

         this.errorMessage = '';
         this.successMessage = '';

         if (!this.selectedVendedorId || !this.startSerial || !this.endSerial) {
           console.log('Campos incompletos, mostrando mensaje de error');
           this.errorMessage = 'Por favor, completa todos los campos.';
           return;
         }

         // Validar formato de seriales (LM seguido de números)
         console.log('Validando formato de seriales...');
         const serialRegex = /^LM\d+$/;
         if (!serialRegex.test(this.startSerial) || !serialRegex.test(this.endSerial)) {
           console.log('Formato de seriales inválido');
           this.errorMessage = 'Los seriales deben comenzar con "LM" seguido de números (ej. LM0001).';
           return;
         }

         // Extraer números de los seriales
         console.log('Extrayendo números de seriales...');
         const startNum = parseInt(this.startSerial.replace('LM', ''), 10);
         const endNum = parseInt(this.endSerial.replace('LM', ''), 10);
         console.log('Números extraídos:', { startNum, endNum });

         if (isNaN(startNum) || isNaN(endNum) || startNum > endNum) {
           console.log('Validación de rango fallida');
           this.errorMessage = 'El serial inicial debe ser menor o igual al serial final.';
           return;
         }

         try {
           console.log('Generando lista de seriales...');
           const serials = [];
           for (let i = startNum; i <= endNum; i++) {
             const serial = `LM${i.toString().padStart(4, '0')}`;
             serials.push(serial);
           }
           console.log('Seriales generados:', serials);

           let allCartones = [];
           let alreadyAssignedSerials = [];

           // Consultar cada cartón individualmente
           for (const serial of serials) {
             console.log(`Consultando cartón con serial ${serial}...`);
             const cartonRef = doc(db, 'cartones', serial);
             const cartonDoc = await getDoc(cartonRef);
             if (cartonDoc.exists()) {
               const cartonData = cartonDoc.data();
               console.log(`Cartón ${serial} encontrado:`, cartonData);
               if (cartonData.vendedorId && cartonData.vendedorId !== this.selectedVendedorId) {
                 alreadyAssignedSerials.push(serial);
               } else {
                 allCartones.push({ id: cartonDoc.id, data: () => cartonData });
               }
             } else {
               console.log(`Cartón ${serial} no encontrado`);
             }
           }

           if (allCartones.length === 0 && alreadyAssignedSerials.length === 0) {
             console.log('No se encontraron cartones para el rango:', serials);
             this.errorMessage = 'No se encontraron cartones en el rango especificado.';
             return;
           }

           if (alreadyAssignedSerials.length > 0) {
             console.log('Cartones ya asignados detectados:', alreadyAssignedSerials);
             this.errorMessage = `Los siguientes cartones ya están asignados a otro vendedor: ${alreadyAssignedSerials.join(', ')}`;
             return;
           }

           // Actualizar vendedorId para cada cartón
           console.log('Procediendo a asignar cartones...');
           for (const carton of allCartones) {
             console.log(`Asignando cartón ${carton.id} a ${this.selectedVendedorId}`);
             await updateDoc(doc(db, 'cartones', carton.id), {
               vendedorId: this.selectedVendedorId
             });
             console.log(`Cartón ${carton.id} asignado exitosamente`);
           }

           console.log('Asignación completada para', allCartones.length, 'cartones');
           this.successMessage = 'Cartones asignados';
           this.startSerial = '';
           this.endSerial = '';
           this.selectedVendedorId = '';
         } catch (error) {
           console.error('Error general al asignar cartones:', error);
           this.errorMessage = 'Error al asignar cartones: ' + error.message;
         }
       }
     }
   };
   </script>

   <style scoped>
   /* Tailwind maneja los estilos */
   </style>
